import 'package:flutter/material.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Carrinho de Compras',
      debugShowCheckedModeBanner: false,
      theme: ThemeData(
        colorScheme: ColorScheme.fromSeed(seedColor: Colors.blue),
        useMaterial3: true,
      ),
      home: const CarrinhoPage(),
    );
  }
}

class Produto {
  final String nome;
  final double preco;
  final String imagem;

  Produto(this.nome, this.preco, this.imagem);
}

class CarrinhoPage extends StatefulWidget {
  const CarrinhoPage({super.key});

  @override
  State<CarrinhoPage> createState() => _CarrinhoPageState();
}

class _CarrinhoPageState extends State<CarrinhoPage> {
  final List<Produto> produtos = [
    Produto("Maçã", 2.50, "data:image/jpeg;base64,"),
    Produto("Banana", 1.80, "data:image/jpeg;base64,"),
    Produto("Laranja", 3.20, "data:image/jpeg;base64,"),
    Produto("Pão", 4.00, "data:image/jpeg;base64,"),
    Produto("Leite", 5.00, "data:image/jpeg;base64,"),
    Produto("Café", 6.00, "data:image/jpeg;base64,"),
  ];

  final List<bool> adicionados = List.filled(6, false);

  double get total {
    double t = 0;
    for (int i = 0; i < produtos.length; i++) {
      if (adicionados[i]) {
        t += produtos[i].preco;
      }
    }
    return t;
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        centerTitle: true,
        backgroundColor: Colors.blue,
        title: Container(
          padding: const EdgeInsets.symmetric(horizontal: 8, vertical: 4),
          decoration: BoxDecoration(
            color: Colors.white,
            border: Border.all(color: Colors.white, width: 2),
            borderRadius: BorderRadius.circular(8),
          ),
          child: const Text(
            'Carrinho de Compras',
            style: TextStyle(color: Color.fromARGB(255, 24, 155, 255), fontWeight: FontWeight.bold),
          ),
        ),
      ),
      body: Column(
        children: [
          Expanded(
            child: GridView.builder(
              padding: const EdgeInsets.all(8.0),
              itemCount: produtos.length,
              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                mainAxisSpacing: 8,
                crossAxisSpacing: 8,
                childAspectRatio: 0.75,
              ),
              itemBuilder: (context, index) {
                final produto = produtos[index];
                return Card(
                  shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(12),
                    side: const BorderSide(color: Colors.grey),
                  ),
                  elevation: 4,
                  child: Column(
                    mainAxisAlignment: MainAxisAlignment.center,
                    children: [
                      Expanded(
                        child: ClipRRect(
                          borderRadius: BorderRadius.circular(12),
                          child: Image.network(
                            produto.imagem,
                            fit: BoxFit.cover,
                            width: double.infinity,
                          ),
                        ),
                      ),
                      const SizedBox(height: 8),
                      Text(produto.nome, style: const TextStyle(fontWeight: FontWeight.bold)),
                      Text("R\$ ${produto.preco.toStringAsFixed(2)}"),
                      const SizedBox(height: 4),
                      ElevatedButton.icon(
                        onPressed: () {
                          setState(() {
                            adicionados[index] = !adicionados[index];
                          });
                        },
                        icon: const Icon(Icons.add_shopping_cart),
                        label: Text(adicionados[index] ? "Remover" : "Adicionar"),
                        style: ElevatedButton.styleFrom(
                          backgroundColor: Color.fromARGB(255, 65, 173, 255),
                          foregroundColor: Colors.white,
                          minimumSize: const Size(120, 36),
                        ),
                      ),
                    ],
                  ),
                );
              },
            ),
          ),
          Container(
            padding: const EdgeInsets.all(16),
            color: Color.fromARGB(255, 65, 173, 255),
            child: Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                Text(
                  "Total: R\$ ${total.toStringAsFixed(2)}",
                  style: const TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
                ),
              ],
            ),
          ),
        ],
      ),
    );
  }
}